import React, { useCallback, useEffect, useRef } from "react";
import LogicFlow from "@logicflow/core";
import "@logicflow/core/dist/style/index.css";
import { DndPanel, SelectionSelect, Menu, Control } from "@logicflow/extension";
import "@logicflow/extension/lib/style/index.css";
import { Button } from "antd";

LogicFlow.use(DndPanel);
LogicFlow.use(SelectionSelect);
LogicFlow.use(Menu);
LogicFlow.use(Control);

export default function App() {
  const refContainer = useRef();
  const logicflow = useRef();

  useEffect(() => {
    logicflow.current = new LogicFlow({
      container: refContainer.current,
      keyboard: { enabled: true }, // 开启键盘快捷键
      grid: true,
      width: 1500,
      height: 500,
    });

    logicflow.current.setTheme({
      rect: {
        fill: "#FFFFFF",
        stroke: "#1976d2",
        strokeWidth: 2,
      },
    });

    logicflow.current.extension.dndPanel.setPatternItems([
      {
        type: "ellipse",
        label: "椭圆",
        icon: "data:image/png;base64,",
      },
      {
        type: "circle",
        label: "圆形",
        icon: "data:image/png;base64,",
      },
      {
        type: "rect",
        label: "矩形",
        icon: "data:image/png;base64,",
        className: "important-node",
      },
      {
        type: "diamond",
        label: "菱形",
        icon: "data:image/png;base64,",
      },
    ]);

    logicflow.current.render({});
  }, []);

  const getData = useCallback(() => {
    const data = logicflow.current.getGraphRawData();
    console.log(data);
  }, []);

  return (
    <div>
      <div className="App" ref={refContainer}></div>
      <Button onClick={getData}>获取数据</Button>
    </div>
  );
}
